import React, { useContext } from "react";
import { Link } from "react-router-dom";
import { ContextGlobal } from "./utils/global.context";
import { StarIcon, StarOffIcon } from 'lucide-react'; // Optional, for icons
import userPic from "../images/doctor.jpg";

const Card = ({ id, name, username }) => {
  const { state, dispatch } = useContext(ContextGlobal);

  const addFav = () => {
    dispatch({ type: "ADD_FAV", payload: { id, name, username } });
  };

  const removeFav = () => {
    dispatch({ type: "REMOVE_FAV", payload: { id } });
  };

  const isFav = state.favs.some((fav) => fav.id === id);

  return (
    <div className="card">
      <Link to={`/dentist/${id}`} className="card-link">
      <img src={userPic} alt="" />

        <h3>{name}</h3>
        <p>{username}</p>
      </Link>
      <button 
        onClick={isFav ? removeFav : addFav} 
        className="favButton"
      >
        {isFav ? (
          <>
            <StarOffIcon className="mr-2" /> Remove from Favorites
          </>
        ) : (
          <>
            <StarIcon className="mr-2" /> Add to Favorites
          </>
        )}
      </button>
    </div>
  );
};

export default Card;
