import React, { useContext } from "react";
import { Link } from "react-router-dom";
import { ContextGlobal } from "../Components/utils/global.context";
import DHLogo from "../images/DH.png";
import { MoonIcon, SunIcon } from 'lucide-react'; // Assuming you'll install lucide-react

const Navbar = () => {
  const { state, dispatch } = useContext(ContextGlobal);

  const toggleTheme = () => {
    const newTheme = state.theme === "light" ? "dark" : "light";
    dispatch({ type: "SET_THEME", payload: newTheme });
  };

  return (
    <nav className={state.theme}>
      <img className="logo" src={DHLogo} alt="Digital House Logo" />
      <ul>
        <li><Link to="/home">Home</Link></li>
        <li><Link to="/contact">Contact</Link></li>
        <li><Link to="/favs">Favorites</Link></li>
        <button 
          onClick={toggleTheme} 
          className="theme-toggle"
          aria-label={state.theme === "light" ? "Switch to Dark Mode" : "Switch to Light Mode"}
        >
          {state.theme === "light" ? <MoonIcon /> : <SunIcon />}
        </button>
      </ul>
    </nav>
  );
};

export default Navbar;
